const products=[{id:1,name:"Black Galaxy",category:"black",price:120,rating:4.2,img:"images/granite1.jpg"},{id:2,name:"Red Multicolor",category:"red",price:100,rating:3.9,img:"images/granite2.jpg"},{id:3,name:"Black Pearl",category:"black",price:140,rating:4.7,img:"images/granite3.jpg"}];let cart=JSON.parse(localStorage.getItem("cart"))||[];const renderProducts=()=>{const list=document.getElementById("product-list");list.innerHTML="";let filtered=products;const cat=document.getElementById("categoryFilter").value;if(cat!=="all")filtered=filtered.filter(p=>p.category===cat);const sort=document.getElementById("sortOption").value;filtered=filtered.sort((a,b)=>a[sort]-b[sort]);filtered.forEach(p=>{const card=document.createElement("div");card.className="card";card.innerHTML=`<img src="${p.img}" loading="lazy"/><h3>${p.name}</h3><p>₹${p.price}</p><p>⭐ ${p.rating}</p><button onclick="addToCart(${p.id})">Add to Cart</button>`;list.appendChild(card)})};const addToCart=id=>{const item=products.find(p=>p.id===id);cart.push(item);localStorage.setItem("cart",JSON.stringify(cart));renderCart()};const renderCart=()=>{const list=document.getElementById("cart-list");list.innerHTML="";cart.forEach(p=>{const li=document.createElement("li");li.textContent=`${p.name} - ₹${p.price}`;list.appendChild(li)})};document.getElementById("categoryFilter").addEventListener("change",renderProducts);document.getElementById("sortOption").addEventListener("change",renderProducts);renderProducts();renderCart();

